descriptor.templateCss:
#container {
    overflow: auto;
}

.tbDatasource-container {
    width: 100%;
    height: 100%;
    overflow: hidden;
}

.tbDatasource-table {
    width: 100%;
    height: 100%;
    border-collapse: collapse;
    white-space: nowrap;
    font-weight: 100;
    text-align: right;
}

.tbDatasource-table td {
    padding: 12px;
    position: relative;
    box-sizing: border-box;
}

.tbDatasource-data-key {
    opacity: 0.7;
    font-weight: 400;
    font-size: 3.500rem;
}

.tbDatasource-value {
    font-size: 5.000rem;
}

descriptor.controllerScript:
self.onInit = function() {

    self.ctx.labelPosition = self.ctx.settings.labelPosition ||
        'left';
    self.ctx.$scope.labelPosition = self.ctx.labelPosition;
    self.ctx.$scope.newValue = 0;
    self.ctx.$scope.valueCellStyle = {};
    self.ctx.$scope.labelCellStyle = {};
    self.ctx.$scope.lowLimit = "";
    self.ctx.$scope.highLimit = "";
    self.ctx.$scope.pattern = new RegExp("^(\\+|-)?\\d+$");
    if (self.ctx.decimals > 0) {
        self.ctx.$scope.pattern = new RegExp("^-?\\d*\\.{0,1}\\d{1," +
            self.ctx.decimals + "}$");
    }
    //console.log(self.ctx.$scope.pattern);
    self.ctx.attributeData = {};
    self.ctx.attributeData.valid = false;
    self.ctx.log = {};

    self.ctx.attributeData.Scope = "SERVER_SCOPE";
    if (self.ctx.settings.attributeScope == 'Client')
        self.ctx.attributeData.Scope = 'CLIENT_SCOPE';
    if (self.ctx.settings.attributeScope == 'Shared')
        self.ctx.attributeData.Scope = 'SHARED_SCOPE';
    if (self.ctx.settings.lowLimitEnable) self.ctx.$scope
        .lowLimit = self.ctx.settings.lowLimit;
    if (self.ctx.settings.highLimitEnable) self.ctx.$scope
        .highLimit = self.ctx.settings.highLimit;
    self.ctx.$scope.DialogTitle = self.ctx.settings.DialogTitle ||
        "Mise a jour";
    var attributeService = self.ctx.$scope.$injector.get(
        'attributeService');
    var $mdDialog = self.ctx.$scope.$injector.get(
        '$mdDialog');
    self.ctx.CurrentUser= self.ctx.$scope.$injector.get(
        'userService').getCurrentUser();
    self.ctx.username=(self.ctx.CurrentUser.firstName || "")+" "+(self.ctx.CurrentUser.lastName || "");
    self.ctx.username+=" "+(self.ctx.CurrentUser.sub || "");
    self.ctx.username=self.ctx.username.trim();
    //console.log(JSON.stringify(self.ctx.username)); 

    self.ctx.$scope.dialogData = {};
    self.ctx.$scope.progressDisabled=true;
    if (self.ctx.datasources.length > 0) {
        var tbDatasource = self.ctx.datasources[0];
        //console.log(JSON.stringify(tbDatasource));

        self.ctx.datasourceContainer = $(
            '#datasourceId', self.ctx.$container);
        var table = $('#tableId', self.ctx.$container);

        if (tbDatasource.dataKeys.length > 0) {
            var dataKey = tbDatasource.dataKeys[0];
            self.ctx.logEnabled = dataKey.settings.logEnable ||
                false;
            self.ctx.logLabel = dataKey.settings.logLabel || dataKey.label;
            self.ctx.$scope.label = dataKey.label;
            self.ctx.labelCell = $('#labelCellId',
                table);
            self.ctx.valueCell = $('#cellId', table);
            if (dataKey.type == 'attribute') {
                self.ctx.attributeData.entityType =
                    tbDatasource.entityType;
                self.ctx.attributeData.entityId =
                    tbDatasource.entityId;
                self.ctx.attributeData.dataKey = [{}];
                self.ctx.attributeData.dataKey[0].key =
                    dataKey.name;
                self.ctx.attributeData.valid = true;
                //console.log(JSON.stringify(self.ctx.attributeData));
            }
        }
    }

    self.ctx.$scope.showPrerenderedDialog = function() {
        if (self.ctx.attributeData.valid) {
        if (!self.ctx.isEdit) {
            self.ctx.$scope.dialogData.value = self.ctx.$scope.newValue;
            self.ctx.oldValue = self.ctx.$scope.newValue;
            self.ctx.$scope.progressDisabled=true;
            $mdDialog.show({
                contentElement: $('#myStaticDialog',self.ctx.$container),
                parent: angular.element(
                    document.body)
            });
        }}
    };

    self.ctx.$scope.close = function() {
        $mdDialog.cancel();
    };
    self.ctx.$scope.submit = function() {
        var logRequest;
        if (self.ctx.attributeData.valid) {
            //console.log("update attribute");
            self.ctx.attributeData.dataKey[0].value =
                self.ctx.$scope.dialogData.value;
            //console.log(JSON.stringify(self.ctx.attributeData));
            self.ctx.$scope.progressDisabled=false;
            var requete = attributeService.saveEntityAttributes(
                self.ctx.attributeData.entityType,
                self.ctx.attributeData.entityId,
                self.ctx.attributeData.Scope,
                self.ctx.attributeData.dataKey);
requete.then(
                            function success(response) {
                              /*  console.log("Reponse : " +
                                    JSON.stringify(
                                        response));*/
                                if (self.ctx.logEnabled) {
                self.ctx.log.Parametre=self.ctx.logLabel;
                self.ctx.log['Ancienne valeur']=self.ctx.oldValue;
                self.ctx.log['Nouvelle valeur']=self.ctx.$scope.dialogData.value;
                self.ctx.log.Utilisateur=self.ctx.username;
                var tsData=[{}];
                tsData[0].key="log";
                tsData[0].value=JSON.stringify(self.ctx.log,undefined,2);
                //console.log(JSON.stringify(tsData));
                logRequest = attributeService.saveEntityAttributes(
                    self.ctx.attributeData.entityType,
                    self.ctx.attributeData.entityId,
                    "timeseries/SERVER_SCOPE",
                    tsData);   
                 }         
                                self.ctx.$scope.progressDisabled=true;
                                $mdDialog.cancel();
                            },
                            function fail(rejection) {
                              /*  console.log("Rejection : " +
                                    JSON.stringify(
                                        rejection));*/
                                self.ctx.$scope.progressDisabled=true;
                                $mdDialog.cancel();
                            }
                        );
        } else {
            // console.log("invalid attribute");
            $mdDialog.cancel();
        }
    };

    $.fn.textWidth = function() {
        var html_org = $(this).html();
        var html_calc = '<span>' + html_org +
            '</span>';
        $(this).html(html_calc);
        var width = $(this).find('span:first').width();
        $(this).html(html_org);
        return width;
    };
    //console.log("call Resize");
    self.onResize();
};

self.onDataUpdated = function() {
    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }

    if (self.ctx.valueCell && self.ctx.data.length > 0) {
        var cellData = self.ctx.data[0];
        if (cellData.data.length > 0) {
            var tvPair = cellData.data[cellData.data.length -
                1];
            var value = tvPair[1];
            var txtValue;
            if (isNumber(value)) {

                self.ctx.$scope.newValue = Number(self.ctx
                    .utils.formatValue(
                        value, self.ctx.decimals,
                        ""));
                txtValue = self.ctx.utils.formatValue(
                    value, self.ctx.decimals, self.ctx
                    .units);
            } else {
                txtValue = value;
            }

            self.ctx.$scope.cellValue = txtValue;
            self.ctx.valueCell.html(txtValue);
            var targetWidth;
            var minDelta;
            if (self.ctx.labelPosition === 'left') {
                targetWidth = self.ctx.datasourceContainer
                    .width() - self.ctx.labelCell.width();
                minDelta = self.ctx.width / 16 + self.ctx
                    .padding;
            } else {
                targetWidth = self.ctx.datasourceContainer
                    .width();
                minDelta = self.ctx.padding;
            }
            var delta = targetWidth - self.ctx.valueCell
                .textWidth();
            var fontSize = self.ctx.valueFontSize;
            if (targetWidth > minDelta) {
                while (delta < minDelta && fontSize > 6) {
                    fontSize--;
                    self.ctx.valueCell.css('font-size',
                        fontSize + 'px');
                    delta = targetWidth - self.ctx.valueCell
                        .textWidth();
                }
            }
        }
    }
    self.ctx.$scope.$digest();
};

self.onResize = function() {
    //console.log("Resize");
    var labelFontSize;
    if (self.ctx.labelPosition === 'top') {
        self.ctx.padding = self.ctx.height / 20;
        labelFontSize = self.ctx.height / 4;
        self.ctx.valueFontSize = self.ctx.height / 2;
    } else {
        self.ctx.padding = self.ctx.width / 50;
        labelFontSize = self.ctx.height / 2.5;
        self.ctx.valueFontSize = self.ctx.height / 2;
        if (self.ctx.width / self.ctx.height <= 2.7) {
            labelFontSize = self.ctx.width / 7;
            self.ctx.valueFontSize = self.ctx.width / 6;
        }
    }
    self.ctx.padding = Math.min(2, self.ctx.padding);
    /* console.log("Label Size :"+labelFontSize);
	console.log("Value Size :"+self.ctx.valueFontSize);
	console.log("Padding :"+self.ctx.padding);*/
    if (self.ctx.labelCell) {
        self.ctx.$scope.labelCellStyle['font-size'] =
            labelFontSize + 'px';
        self.ctx.$scope.labelCellStyle.padding =
            self.ctx.padding + 'px';
    }
    if (self.ctx.valueCell) {
        self.ctx.$scope.valueCellStyle['font-size'] =
            self.ctx.valueFontSize + 'px';
        self.ctx.$scope.valueCellStyle.padding =
            self.ctx.padding + 'px';
    }
};

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1
    };
};


self.onDestroy = function() {};

